    Underweight,
    NoRebalance
  }

  function checkRebalance(
    address[] memory assets,
    uint256[] memory weights,
    uint256 limit,
    address sender
  ) external view returns (RebalanceType);

  function rebalance(
    address[] memory assets,
    uint256[] memory weights,
    address sender,
    address receiver
  ) external returns (bool);
}
